//aca es donde va toda la informacion de la app para crear una single page 
import React from 'react';
import  { BrowserRouter, Switch, Route } from 'react-router-dom';
import Layout from './Layout.js'; //el layout es un objeto asi que crearemos los componentes dentro de este objeto abajo en la funcion app() 
import home from '../pages/Home.js';                                                    
import badgeNew from '../pages/BadgeNew.js';
import badgeEdit from '../pages/BadgeEdit.js';
import badges from '../pages/Badges.js';
import NotFound from '../pages/NotFound.js';


function app() { //funcion lo podemos usar cuando no halla ningun otro metodo o state
    //debemos de tener un elemento hijo para que funcione, osea el <div>elemento hijo</div> este seria el hijo de browserrouter
    //abajo debemos colocar la ruta exacta de la pagina osea colocamos EXACT PATH=
    //el switch renderiza solo una ruta : https://github.com/ReactTraining/react-router/blob/master/packages/react-router/docs/api/Switch.md
    return (
        <BrowserRouter> 
   <Layout> 
        <Switch> 
        <Route exact path="/home" component={home}/>
        <Route exact path="/badges" component={badges}/>   {/* https://github.com/ReactTraining/react-router/blob/master/packages/react-router/docs/api/Route.md */}
        <Route exact path="/badges/new" component={badgeNew}/>
        <Route exact path="/badges/:badgeId/edit" component={badgeEdit} />
        <Route component={NotFound}/>
        </Switch>
    </Layout>
        </BrowserRouter>
    );
}
export default app;
// {/* //para que el layout aparezca debemos crear un prop en el archivo .js del layout
//  */}
