import React from 'react';
import './styles/Badges.css'
import logo from '../images/output-onlinepngtools.png';
import BadgesList from '../components/BadgesList.js';
import PageLoading from '../components/PageLoading.js';
import PageError from '../components/PageError.js';

import {Link} from 'react-router-dom';
import api from '../api.js';


class badges extends React.Component {
  state = {
    loading: true,
    error: null,
    data: undefined
  }

  componentDidMount() { //la mejor forma de trabajar una Api es con componentDidMount() Documentacion aca: https://es.reactjs.org/docs/react-component.html#componentwillunmount
    //ESTO COMIENZA LA PETICION
    this.fetchData(); //esto debe ir en el componente! este fetchData es un metodo del componente osea una accion a realizar
  }

  fetchData = async () => { //aca declaramos el metodo del componendtDidMount
    // el fetch data comienza con loadin: true y cancela el error
    this.setState({ loading: true, error: null }); //declaramos esto aca porque puede que en un futuro mandemos a llamar fetchData

    try { //con esto comenzamos la llamada ala API {preparamos el entorno para la llamada}
      const data = await api.badges.list(); //LA LLAMADA A ESTA API es asincrona!! asi que debemos utilizar  un async /await 
      this.setState({ loading: false, data: data }); //aca si SI! obtenemos los datos los guardamos y en caso de obtenerlos suspendemos el loading
    } catch (error) {
      this.setState({ loading: false, error: error });
    }
  };


  render() {
    if (this.state.loading === true) {
      return <PageLoading />;
    }

    if (this.state.error) {
      return <PageError error={this.state.error} />;
    }

    return (
          <React.Fragment>

             {/* <Navbar/> esto ya se esta compartiendo desde el archivo layout */}
            <div className="Badges">
              <div className="Badges__hero">
                <div className="Badges__container">
                  <img
                    className="Badges_conf-logo"
                    src={logo}
                    alt="Conf Logo"
                  />
                </div>
              </div>
            </div>

            <div className="Badges__container">
                    <div  className="Badges__buttons">
                    <Link to="/badges/new" className="btn btn-primary">
                      New Badge
                    </Link>
                    </div>

                    <div className="Badges__list">
                        <div className="Badges__container">
                        <BadgesList Badges={this.state.data}/>
                        </div>
                   </div>
            </div>
            </React.Fragment>
         )
    }
}

export default badges;




