import React from 'react';
import '../pages/styles/BadgeNew.css';
import Header from '../images/output-onlinepngtools.png';
import Badge from '../components/Badge.js';
import BadgeForm from '../components/BadgeForm.js';
import PageLoading from '../components/PageLoading.js';

import api from '../api.js'; // aca traemos esta Api porque debemos hacer una llamada una vez el usuario haga el submit, recordad que abajo paramos el llamado con el manejo 
// import { Link } from 'react-router-dom';



class BadgeNew extends React.Component {
  State = {
    Loading: false,
    Error: null,
    Form: {
      firstName: '',
      lastName: '',
      email: '',
      jobTitle: '',
      twitter: '',
    },
  };

  handleChange = e => {
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value,
      },
    });
  };

  handleSubmit = async e => {
    e.preventDefault();
    this.setState({ Loading: true, Error: null });

    try {
      await api.badges.create(this.state.form);
      this.setState({ Loading: false });

      this.props.history.push('/badges');
    } catch (Error) {
      this.setState({ Loading: false, Error: Error });
    }
  };

  render() {
    if (this.state.Loading) {
      return <PageLoading />;
    }

    return (
      <React.Fragment>
        <div className="BadgeNew__hero">
          <img
            className="BadgeNew__hero-image img-fluid"
            src={Header}
            alt="Logo"
          />
        </div>

        <div className="container">
          <div className="row">
            <div className="col-6">
              <Badge
                firstName={this.state.form.firstName || 'FIRST_NAME'}
                lastName={this.state.form.lastName || 'LAST_NAME'}
                twitter={this.state.form.twitter || 'twitter'}
                jobTitle={this.state.form.jobTitle || 'JOB_TITLE'}
                email={this.state.form.email || 'EMAIL'}
                avatarUrl="https://www.gravatar.com/avatar/21594ed15d68ace3965642162f8d2e84?d=identicon"
              />
            </div>

            <div className="col-6">
              <h1>New Attendant</h1>
              <BadgeForm
                onChange={this.handleChange}
                onSubmit={this.handleSubmit}
                formValues={this.state.form}
                Error={this.state.Error}
              />
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default BadgeNew;
