import React from 'react';
import '../pages/styles/BadgeNew.css';
import Header from '../images/output-onlinepngtools.png';
import Badge from '../components/Badge.js';
import BadgeForm from '../components/BadgeForm.js';
import PageLoading from '../components/PageLoading.js';

import api from '../api.js'; // aca traemos esta Api porque debemos hacer una llamada una vez el usuario haga el submit, recordad que abajo paramos el llamado con el manejo 
// import { Link } from 'react-router-dom';



class BadgeNew extends React.Component {
  state = {
    loading: false, //esto representa que se envian los datos 
    error: null, //el error es nulo, esto signufica que comenzamos sin error 
    form: {
      FirstName: '',
      LastName: '',
      Email: '',
      JobTitle: '',
      Twitter: '',
    },
  };

  handleChange = e => {  //handleChange() — that is used to set a new state for the input. //handleChange() function to set a new state for input */
    this.setState({ //https://es.reactjs.org/docs/faq-state.html Dcocumentacion para entender!
      form: {
        ...this.state.form, //cuando hacemos esto aca dejamos caer todos los valores del state form
        [e.target.name]: e.target.value,
      },
    });
  };

  handleSubmit = async e => { // esto es la funcion que va a recibir un e osea un evento , estamos llamndo el API asi que debemos utilizar async y await porque es asincrono 
        e.preventDefault(); //y este evento lo debemos detener! porque el navegador va a enviar los datos a una pagina que no hemos especificado 
        this.setState({loading: true, error: null })// como aca comienza el load debe ser en true y el error es null, abajo se maneja y se atrapa si algo sale mal 
        try {
           await api.badges.create(this.state.form) //aqui le estamos pasando los datos del nuevo badge que se esta creando lo tenemos guardado en this.state.form
           this.setState({loading: false}) //aca se detiene  el loading una vez se envian los datos 
          } catch (error){ 
          this.setState({loading: false, error: error }) //aca atrapamos el error osea que el loading lo detenemos pasandole falso y guardamos el error que es igual a error 
        }

      };


 render() { // en el caso de que loading se encienda osea sea true! queremos devolver el formulario la visualizacion osea el loader
if (this.state.loading) { //aca dice que si el loading es true retornamos el componente 
  return <PageLoading />
}
    return (
      <React.Fragment>

        {/* <Navbar/> esto ya se esta compartiendo desde el archivo layout */}
        <div className ="BadgeNew__hero">
            <img className="img-fluid" src={Header} alt="Logo"/>
        </div>
        <div className= "container">
            <div className="row">
                <div className="col-6">
                    <Badge 
                     firstName = {this.state.form.firstName || 'FISRT_NAME'} //ACA EN ESTAS LINEAS VAMOS A DARLE UN VALOR POR OMISION con un condicional que es el || esto significa or, dice que si el valor anterior no existe entonces imprime lo que digamos 
                     lastName = {this.state.form.lastName || 'LAST_NAME'} 
                     jobTitle = {this.state.form.jobTitle ||'Twitter'} 
                     twitter = {this.state.form.twitter || 'JOB_TITLE'} 
                     email= {this.state.form.email|| 'EMAIL'} 
                     AvatarURL = "https://www.gravatar.com/avatar/e79975d3c8ba4392890e18143730eb0c?s=300" // PARA CAMBIAR ESTE AVATAR DEBEMOS HACERLO EN BADGE.JS
                     /> 
                     </div>
              <div className="col-6">
              <BadgeForm
                onChange={this.handleChange}
                onSubmit={this.handleSubmit} //aca manejamos el submit del form y debemos asegurarnos de que cuando el form se envie llamemos el prop del componente BadgeForm
                formValues={this.state.form}
              />
            </div>
          </div>
        </div>
        </React.Fragment> 

    );
  }
}
//https://es.reactjs.org/docs/fragments.html  (React.Fragment)
export default BadgeNew;
