import React from 'react';
import './styles/Badges.css'
import logo from '../images/output-onlinepngtools.png';
import BadgesList from '../components/BadgesList.js';
import {Link} from 'react-router-dom';

class Badges extends React.Component {

  //hay un orden en estos metodos, el constructor ocurre primero #1
  //este constructor debe llevar props para poder inicializar la super clase
  constructor(props) {
    super(props);
    console.log('1. constructor()');
//aca para llamar state debemos incluir el THIS!!
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    console.log('3. componentDidMount()');

    this.timeoutId = setTimeout(() => {
      this.setState({
        data: [
          {
            id: '2de30c42-9deb-40fc-a41f-05e62b5939a7',
            firstName: 'Freda',
            lastName: 'Grady',
            email: 'Leann_Berge@gmail.com',
            jobTitle: 'Legacy Brand Director',
            twitter: 'FredaGrady22221-7573',
            avatarUrl:
              'https://www.gravatar.com/avatar/f63a9c45aca0e7e7de0782a6b1dff40b?d=identicon',
          },
          {
            id: 'd00d3614-101a-44ca-b6c2-0be075aeed3d',
            firstName: 'Major',
            lastName: 'Rodriguez',
            email: 'Ilene66@hotmail.com',
            jobTitle: 'Human Research Architect',
            twitter: 'MajorRodriguez61545',
            avatarUrl:
              'https://www.gravatar.com/avatar/d57a8be8cb9219609905da25d5f3e50a?d=identicon',
          },
          {
            id: '63c03386-33a2-4512-9ac1-354ad7bec5e9',
            firstName: 'Daphney',
            lastName: 'Torphy',
            email: 'Ron61@hotmail.com',
            jobTitle: 'National Markets Officer',
            twitter: 'DaphneyTorphy96105',
            avatarUrl:
              'https://www.gravatar.com/avatar/e74e87d40e55b9ff9791c78892e55cb7?d=identicon',
          },
        ],
      });
    }, 3000);
  }

  componentDidUpdate(prevProps, prevState) {
    console.log('5. componentDidUpdate()');
    console.log({
      prevProps: prevProps,
      prevState: prevState,
    });

    console.log({
      props: this.props,
      state: this.state,
    });
  }
//este componentWillUnmount() es el tercero en ejecutarse
  componentWillUnmount() {
    console.log('3. componentWillUnmount');
    clearTimeout(this.timeoutId);
  }

//el render es el segundo en ejecutarse 
    render() {
      console.log('2. render ');

        return (
          <React.Fragment>

             {/* <Navbar/> esto ya se esta compartiendo desde el archivo layout */}
            <div className="Badges">
              <div className="Badges__hero">
                <div className="Badges__container">
                  <img
                    className="Badges_conf-logo"
                    src={logo}
                    alt="Conf Logo"
                  />
                </div>
              </div>
            </div>

            <div className="Badges__container">
                    <div  className="Badges__buttons">
                    <Link to="/badges/new" className="btn btn-primary">
                      New Badge
                    </Link>
                    </div>

                    <div className="Badges__list">
                        <div className="Badges__container">
                        <BadgesList Badges={this.state.data}/>
                        </div>
                   </div>
            </div>
            </React.Fragment>
         )
    }
}

export default Badges;
