import React from 'react'

class badgeForm extends React.Component {
    handleChange = e => { //onChange y se maneja con un método de la clase this.handleChange
    //  console.log({
    //      name: e.target.name,
    //      value: e.target.value,
    //  });
    this.setState({ //Para guardar la información en el estado se usa una función de la clase component llamada setState a la cual se le debe pasar un objeto con la información que se quiere guardar.
        firstName: e.target.value,
    })
    };

    handleClick = e => { //manejador del evento al escribir 
        console.log("Button was clicked");
       };

       handleSubmit = e => { //manejador de evento al hacer click, Los elementos button también tienen un evento que es onClick.
        e.preventDefault();
        console.log("form was submitted");

       };
//Cuando hay un botón dentro de un formulario, este automáticamente será de 
//tipo submit. Si no queremos que pase así hay dos maneras de evitarlo: especificando que su valor es de tipo button o manejándolo 
//desde el formulario cuando ocurre el evento onSubmit.
    render(){
        return (
            <div>
                <h1>New Attendant</h1>
                <form onSubmit={this.handleSubmit}>
                    <div className="form-group">
        <label>First Name</label>
        <input onChange={this.handleChange}
         className="form-control" 
         type="text"
        name="fisrtName"/>
                    </div>

                    <div className="form-group">
        <label>Last Name</label>
        <input onChange={this.handleChange} 
        className="form-control" 
        type="text" 
        name="LasttName"/>
                    </div>
                    <div className="form-group">
        <label>Email</label>
        <input onChange={this.handleChange} 
        className="form-control" 
        type="text" 
        name="Email"/>
                    </div>
                    <div className="form-group">
        <label>Job Title</label>
        <input onChange={this.handleChange} 
        className="form-control" 
        type="text" 
        name="Job Title"/>
                    </div>


                    <button onClick={this.handleClick} className= "btn btn-primary">Save</button>
                </form>
            </div>
        );
    }
}

export default badgeForm;
