//este componente es para colocar lo que se repite en la web

import React from 'react';
import Navbar from './Navbar.js';
//esto es una herramienta para quitar los <div></div> que son inecesarios o sobran
//https://es.reactjs.org/docs/fragments.html  DOCUMENTACION
function Layout (props){
    return (
        // esta seria la ubicacion del props donde se colocaria
         <React.Fragment>  
            <Navbar/>
        {props.children} 
        </React.Fragment>
    )
}

export default Layout;
