import React from 'react';
import  { BrowserRouter, Route } from 'react-router-dom';
import Badges from '../pages/Badges.js';
import BadgeNew from '../pages/BadgeNew.js';

function App() { //funcion lo podemos usar cuando no halla ningun otro metodo o state
    //debemos de tener un elemento hijo para que funcione, osea el <div>elemento hijo</div>
    return (
        <BrowserRouter> 
        <div> 
        <Route patch="/badges" component={Badges}/>
        <Route patch="/badges/new" component={BadgeNew}/>
        </div>

        </BrowserRouter>
    )
}

export default App;
