import React from 'react';
import md5 from 'md5'; //este es el paquete que se instalo del npm 

// Aprende más del Gravatar en: http://gravatar.com
function Gravatar(props) {   //funcion para el Gravatar
  const email = props.Email;
  const hash = md5(email); //aca definimos el email significa que con el email tremos el hash 

  return (
    <img
      className={props.className}
      src={`https://www.gravatar.com/avatar/${hash}?d=identicon`}
      alt="Avatar"
    />
  );
}

export default Gravatar;
